property.code:
document.observe('xwiki:dom:loaded', function() {
    var solvedInput = $$('.fieldset input[id$="_solved"][type="checkbox"]')[0];
    var title = "$!escapetool.javascript($services.localization.render('patient.save.warning.missingFields.title'))";

    if (XWiki.contextaction == "edit" && solvedInput) {

      // checks if case is marked as solved
      var isSolved = function() {
        return solvedInput.checked;
      };

      // checks if any genes entered are solved
      var hasSolvedGene = function() {
        var hasSolvedGenes = false;
        $$('.Status select[id$="_status"]').each(function (status) {
            if (status.value == "solved" && status.up('tr').down('input.gene-name').value.length > 0) {
              hasSolvedGenes = true;
            }
        });
        return hasSolvedGenes;
      };

      // checks if any OMIM final diagnosis or ORDO final diagnosis entered
      var hasFinalDiagnosis = function() {
        var hasDiagnosis = false;
        $$('.omim_id .accepted-suggestion input[type="checkbox"]').each(function (checkbox) {
            if (checkbox.checked) {
              hasDiagnosis = true;
            }
        });
        return hasDiagnosis;
      };

      document.observe("first:solved:gene:added", function() {
          var message = '';
          if (isSolved() && hasFinalDiagnosis()) { return; }
          if (!isSolved() && !hasFinalDiagnosis()) {
            message = "$!escapetool.javascript($services.localization.render('phenotips.UIXField.solved.hasSolvedGeneNotSolvedNoDiagnosis'))";
          }
          if (!isSolved() && hasFinalDiagnosis()) {
            message = "$!escapetool.javascript($services.localization.render('phenotips.UIXField.solved.hasSolvedGeneNotSolved'))";
          }
          if (isSolved() && !hasFinalDiagnosis()) {
            message = "$!escapetool.javascript($services.localization.render('phenotips.UIXField.solved.hasSolvedGeneNoDiagnosis'))";
          }
          var dialog = new PhenoTips.widgets.ModalPopup(message, false, {'title': title, 'removeOnClose': true});
          dialog.showDialog();
      });

      document.observe('xwiki:dom:updated', function(event) {
          if (!event.memo || !event.memo.elements || !event.memo.elements[0]) {
            return;
          }

          var diagnosisCheckbox = event.memo.elements[0].down('input[name$="_omim_id"]');
          if (!diagnosisCheckbox || !diagnosisCheckbox.checked) {
            return;
          }

          // check if the entered diagnosis is the first
          var isFirstDiagnosis = true;
          $$('.omim_id .accepted-suggestion input[type="checkbox"]').each(function (checkbox) {
            if (checkbox.checked && checkbox != diagnosisCheckbox) {
              isFirstDiagnosis = false;
            }
          });
          if (!isFirstDiagnosis) {
            return;
          }

          var message = '';
          if (isSolved() && hasSolvedGene()) { return; }
          if (!isSolved() && !hasSolvedGene()) {
            message = "$!escapetool.javascript($services.localization.render('phenotips.UIXField.solved.hasDiagnosisNoSolvedGenesNotSolved'))";
          }
          if (!isSolved() && hasSolvedGene()) {
            message = "$!escapetool.javascript($services.localization.render('phenotips.UIXField.solved.hasDiagnosisNotSolved'))";
          }
          if (isSolved() && !hasSolvedGene()) {
            message = "$!escapetool.javascript($services.localization.render('phenotips.UIXField.solved.hasDiagnosisNoSolvedGenes'))";
          }
          var dialog = new PhenoTips.widgets.ModalPopup(message, false, {'title': title, 'removeOnClose': true});
          dialog.showDialog();
      });

      solvedInput.observe("change", function () {
          if (solvedInput.checked) {
            document.fire("solved:case:checked");
          }
      });

      document.observe("solved:case:checked", function() {
          var message = '';
          if (hasSolvedGene() && hasFinalDiagnosis()) { return; }
          if (!hasSolvedGene() && !hasFinalDiagnosis()) {
            message = "$!escapetool.javascript($services.localization.render('phenotips.UIXField.solved.isSolvedNoSolvedGenesNoDiagnosis'))";
          }
          if (!hasSolvedGene() && hasFinalDiagnosis()) {
            message = "$!escapetool.javascript($services.localization.render('phenotips.UIXField.solved.isSolvedNoSolvedGenes'))";
          }
          if (hasSolvedGene() && !hasFinalDiagnosis()) {
            message = "$!escapetool.javascript($services.localization.render('phenotips.UIXField.solved.isSolvedNoDiagnosis'))";
          }
          var dialog = new PhenoTips.widgets.ModalPopup(message, false, {'title': title, 'removeOnClose': true});
          dialog.showDialog();
      });
    };
});

