property.code:
['xwiki:dom:loaded', 'xwiki:dom:updated'].each(function (eventName) {
  document.observe(eventName, function() {
    $$('.hpo-branch select').invoke('observe', 'blur', function(event) {
      var hiddenInput = event.target.up().up().select("input")[0];
      var selected = event.target.selectedOptions[0];
      hiddenInput.title = selected.title;
      hiddenInput.value = selected.value;
    });
  });

  $$('.hpo-branch select').forEach(function(element, index, array){
    var hiddenInput = element.up().up().select("input")[0];
    var selected = element.selectedOptions[0];
    hiddenInput.title = selected.title;
    hiddenInput.value = selected.value;
  });
});
